import React, { useState } from "react";
// import './CustomCalendar.css'; // Create this file for styling if needed

const CustomCalendar = () => {
  const [currentDate, setCurrentDate] = useState(new Date());
  const [visibleStartDate, setVisibleStartDate] = useState(0); // Index of the first visible date

  const goToNextDay = () => {
    const newDate = new Date(currentDate);
    newDate.setDate(newDate.getDate() + 1);

    setCurrentDate(newDate);
    setVisibleStartDate((visibleStartDate + 1) % newDate.getDate());
  };

  const goToPreviousDay = () => {
    const newDate = new Date(currentDate);
    newDate.setDate(newDate.getDate() - 1);

    setCurrentDate(newDate);
    setVisibleStartDate(
      (visibleStartDate -
        1 +
        new Date(newDate.getFullYear(), newDate.getMonth() + 1, 0).getDate()) %
        new Date(newDate.getFullYear(), newDate.getMonth() + 1, 0).getDate()
    );
  };

  const goToNextMonth = () => {
    const newDate = new Date(currentDate);
    newDate.setMonth(newDate.getMonth() + 1);
    setCurrentDate(newDate);
    setVisibleStartDate(
      (visibleStartDate + 1) %
        new Date(newDate.getFullYear(), newDate.getMonth() + 1, 0).getDate()
    );
  };

  const getVisibleDates = () => {
    const daysInMonth = new Date(
      currentDate.getFullYear(),
      currentDate.getMonth() + 1,
      0
    ).getDate();
    const daysInWeek = 7;
    const days = [];

    for (let i = 0; i < daysInWeek; i++) {
      const dayNumber = visibleStartDate + i + 1;
      days.push({
        date: dayNumber > daysInMonth ? "" : dayNumber,
        dayOfWeek: new Date(
          currentDate.getFullYear(),
          currentDate.getMonth(),
          dayNumber
        ).toLocaleDateString("en-US", { weekday: "short" }),
      });
    }

    return days;
  };

  return (
    <div className="custom-calendar">
      <div className="calendar-header">
        <button onClick={goToPreviousDay}>{"<"}</button>
        <button onClick={goToNextDay}>{">"}</button>
        <button onClick={goToNextMonth}>{">>"}</button>
        <h2>
          {new Intl.DateTimeFormat("en-US", {
            month: "long",
            year: "numeric",
          }).format(currentDate)}
        </h2>
      </div>
      <div className="calendar-days">
        {getVisibleDates().map((day, index) => (
          <div key={index} className="day">
            {day.dayOfWeek}
          </div>
        ))}
      </div>
      <div className="calendar-dates">
        {getVisibleDates().map((day, index) => (
          <div key={index} className="date">
            {day.date}
          </div>
        ))}
      </div>
    </div>
  );
};

export default CustomCalendar;
