import React, { useEffect, useState } from "react";
import { useRef } from "react";

function SignUpDetails(props) {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [date, setDate] = useState("");
  const firstInputRef = useRef(null);
  const secondInputRef = useRef(null);

  const handleFirstInputClick = () => {
    // Trigger focus on the second input field when the first input is clicked
    secondInputRef.current.focus();
  };
  const handleDateInputClick = () => {
    if (dateInputRef.current) {
      dateInputRef.current.click();
    }
  };
  // let takeData = props.sendData;
  const [wrongDetails, setWrongDetails] = useState(true);
  useEffect(() => {
    if (props.sendData == true) {
      if (email === "sonu@gmail.com") {
        alert("This email is already present");
        props.setSendData(false);

        setWrongDetails(false);
      } else {
        alert("Form submitted");
      }
    }
  }, [email, props.sendData, props.setSendData]);

  return (
    <>
      <div className="enter-mob-inner-div3">
        <div className="input-title">
          <div>
            <h5>Enter your Full Name</h5>
          </div>
          <div>
            <input
              type="text"
              name="name"
              placeholder="Full Name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
            />
          </div>
        </div>
        <div className="input-title">
          <div>
            {wrongDetails ? (
              <h5>Enter your Email Id</h5>
            ) : (
              <h5 style={{ color: "red" }}> Email Id is alredy present</h5>
            )}
          </div>
          <div>
            <input
              className={`${wrongDetails ? "" : "wrong-details"}`}
              type="email"
              name="email"
              placeholder="Email Id"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
        </div>
        <div className="input-title">
          <div>
            <h5>Date of Birth</h5>
          </div>
          <div className="date-input">
            <input
              type="text"
              placeholder="DD/MM/YYYY"
              onClick={handleDateInputClick}
              readOnly
            />

            {/* Hidden date input field */}
            <input
              type="date"
              ref={dateInputRef}
              style={{ display: "none" }}
              onChange={(e) => {
                // Handle the selected date as needed
                const selectedDate = e.target.value;
                console.log("Selected Date:", selectedDate);

                // Update your state or perform other actions with the selected date
              }}
            />
          </div>
        </div>
      </div>
    </>
  );
}

export default SignUpDetails;
