import React, { useState, useEffect } from "react";
import singer from "../../assets/img/singer2.png";
import "../home/signin.css";
import logo from "../../assets/img/logo-choira.svg";
import google from "../../assets/img/google.png";
import facebook from "../../assets/img/facebook.png";
import apple from "../../assets/img/apple.png";

function Signin() {
  const [seconds, setSeconds] = useState(30);

  useEffect(() => {
    const timer = setTimeout(() => {
      if (seconds > 0) {
        setSeconds(seconds - 1);
      }
    }, 1000);

    // Clear the timeout when the component is unmounted or when the countdown reaches 0
    return () => clearTimeout(timer);
  }, [seconds]);

  const restartTimer = () => {
    setSeconds(30); // Reset the timer to its initial value
  };

  return (
    <>
      <div className="navbar">
        <img src={logo} alt="Choira Logo" />
      </div>

      <div className="wrapper">
        <div className="main">
          <div className="singer">
            <img src={singer} alt="Singer" />
          </div>

          <div className="signup">
            <div className="signup-main">
              <div className="signup-main-2">
                <div>
                  {seconds > 0 ? (
                    <h6>Expect code in {seconds} seconds</h6>
                  ) : (
                    <>
                      <h6>Code expected! Time is up!</h6>
                      <button onClick={restartTimer}>Restart Timer</button>
                    </>
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default Signin;
