import React, { useState } from "react";
import { FaAngleRight } from "react-icons/fa6";
import upload from "../../assets/img/chooseType-img/upload.svg";

export default function ShareLink() {
  const [uploadedFile, setUploadedFile] = useState(null);

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];

    // Store the selected file in state
    setUploadedFile(selectedFile);

    // Handle the selected file as needed
    console.log("Selected File:", selectedFile);
  };

  return (
    <>
      <div className="project-div2">
        <div>
          <h2>
            Share links of similar tracks for inspiration &nbsp;
            <span className="optional">(Optional)</span>
          </h2>
        </div>
        <div className="attach-demo">
          <div className="reference-link">
            <input type="text" placeholder="Enter reference links" />
          </div>
          <div>
            <h3>
              Attach a demo file <span className="optional">(Optional)</span>
            </h3>
          </div>
          <div className="uploaded-file">
            {uploadedFile && <p>{uploadedFile.name}</p>}
          </div>
          <div>
            <input
              type="file"
              id="fileInput"
              name="fileInput"
              accept=".mp3 , .zip ,.rar"
              onChange={handleFileChange}
              style={{ display: "none" }}
            />
            <label htmlFor="fileInput">
              <div className="demo-upload">
                <img src={upload} alt="" /> Attach
              </div>
            </label>
          </div>
          <div>
            <h2>Tell us more about the project</h2>
          </div>
          <div>
            <textarea placeholder="Enter text here..."></textarea>
          </div>
        </div>

        <div className="project-div2-btn">
          <button>
            Continue <FaAngleRight />
          </button>
        </div>
      </div>
    </>
  );
}
