import React, { useState } from "react";
import { FaAngleLeft, FaAngleRight } from "react-icons/fa6";

const BookSession = () => {
  const [currentDate, setCurrentDate] = useState(new Date());
  const [daysNavigated, setDaysNavigated] = useState(0);

  const handleNextDate = () => {
    const nextDate = new Date(currentDate);
    nextDate.setDate(nextDate.getDate() + 1);

    // Check if we have moved to the next month
    if (nextDate.getMonth() !== currentDate.getMonth()) {
      nextDate.setDate(1); // Move to the first day of the next month
    }

    setDaysNavigated(daysNavigated + 1);

    if (daysNavigated >= 7) {
      // Reset to today's date after 7 days
      setCurrentDate(new Date());
      setDaysNavigated(0);
    } else {
      setCurrentDate(nextDate);
    }
  };

  const handlePreviousDate = () => {
    const previousDate = new Date(currentDate);
    previousDate.setDate(previousDate.getDate() - 1);

    // Check if we have moved to the previous month
    if (previousDate.getMonth() !== currentDate.getMonth()) {
      const lastDayOfPrevMonth = new Date(
        currentDate.getFullYear(),
        currentDate.getMonth(),
        0
      ).getDate();
      previousDate.setDate(lastDayOfPrevMonth); // Move to the last day of the previous month
    }

    // Check if the user has navigated to the next date
    if (daysNavigated > 0) {
      setDaysNavigated(daysNavigated - 1);
      setCurrentDate(previousDate);
    }
    // If not, update the UI without changing the current date
    // You can choose to handle this case differently based on your UI requirements
  };

  const getDayOfWeek = (date, offset = 0) => {
    const daysOfWeek = ["Sun", "Mon", "Tue", "Wed", "Thu", "Fri", "Sat"];
    const newDate = new Date(date);
    newDate.setDate(date.getDate() + offset);
    return daysOfWeek[newDate.getDay()];
  };

  const getFormattedDate = (date, offset = 0) => {
    const newDate = new Date(date);
    newDate.setDate(date.getDate() + offset);
    return newDate.getDate();
  };

  const generateTimeSlots = () => {
    const baseTime = 11; // Start time in hours
    const slotCount = 4; // Number of slots per main slot
    const mainSlots = 3; // Number of main slots

    const timeSlots = [];

    for (let i = 0; i < mainSlots; i++) {
      const mainSlot = [];
      for (let j = 0; j < slotCount; j++) {
        const time = `${baseTime + i * 4 + j}:00 AM`; // Adjust the formula based on your requirements
        mainSlot.push(<div key={time}>{time}</div>);
      }
      timeSlots.push(
        <div className="main-slots" key={i}>
          <div className="slots">{mainSlot}</div>
        </div>
      );
    }

    return timeSlots;
  };

  const timeZoneOffset = new Date().getTimezoneOffset();
  const timeZoneHours = Math.floor(Math.abs(timeZoneOffset) / 60);
  const timeZoneMinutes = Math.abs(timeZoneOffset) % 60;
  const timeZoneSign = timeZoneOffset > 0 ? "-" : "+";
  const timeZoneString = `(${timeZoneSign}${String(timeZoneHours).padStart(
    2,
    "0"
  )}:${String(timeZoneMinutes).padStart(2, "0")} IST)`;

  return (
    <>
      <div className="project-div2">
        <div className="project-div2-title">
          <h2>Choose Musician for your project</h2>
          <p>
            Your time zone: {Intl.DateTimeFormat().resolvedOptions().timeZone}{" "}
            {timeZoneString}
          </p>
        </div>
        <div className="bookSession-div">
          <div>
            <h2>Choose your time slot</h2>
          </div>
          <div>
            <h5>
              {new Intl.DateTimeFormat("en-US", { month: "long" }).format(
                currentDate
              )}{" "}
              {currentDate.getFullYear()}
            </h5>
          </div>
          <div className="book-custom-dates">
            <div className="gotoPreviousDate" onClick={handlePreviousDate}>
              <button>
                <FaAngleLeft />
              </button>
            </div>
            <div>
              <div className="previousDate">
                {getDayOfWeek(currentDate, -1)}
              </div>
              <div>{getFormattedDate(currentDate, -1)}</div>
            </div>
            <div className="todaysDate">
              <div>{getDayOfWeek(currentDate)}</div>
              <div>{getFormattedDate(currentDate)}</div>
            </div>
            <div>
              <div className="tomorrowDate">{getDayOfWeek(currentDate, 1)}</div>
              <div>{getFormattedDate(currentDate, 1)}</div>
            </div>
            <div className="gotoNextDate">
              <button>
                <FaAngleRight onClick={handleNextDate} />
              </button>
            </div>
          </div>
          <div>
            <h5>Available time slots</h5>
          </div>
          <div className="timeSlot">{generateTimeSlots()}</div>
        </div>

        <div className="project-div2-btn">
          <button>
            Continue <FaAngleRight />
          </button>
        </div>
      </div>
    </>
  );
};

export default BookSession;
