import React, { useEffect, useState, useRef } from "react";

function SignUpDetails(props) {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [isEmailPresent, setIsEmailPresent] = useState(false);
  const [date, setDate] = useState("");
  const dateInputRef = useRef(null);

  const handleDateInputClick = () => {
    if (dateInputRef.current) {
      dateInputRef.current.click();
    }
  };

  useEffect(() => {
    if (props.sendData) {
      if (email === "sonu@gmail.com") {
        alert("This email is already present");
        setIsEmailPresent(true);
        props.setSendData(false);
      } else {
        alert("Form submitted");
      }
    }
  }, [email, props.sendData, props.setSendData]);

  return (
    <>
      <div className="enter-mob-inner-div3">
        <div className="input-title">
          <div>
            <h5>Enter your Full Name</h5>
          </div>
          <div>
            <input
              type="text"
              name="name"
              placeholder="Full Name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
            />
          </div>
        </div>
        <div className="input-title">
          <div>
            {isEmailPresent ? (
              <h5 style={{ color: "red" }}>Email Id is already present</h5>
            ) : (
              <h5>Enter your Email Id</h5>
            )}
          </div>
          <div>
            <input
              className={isEmailPresent ? "wrong-details" : ""}
              type="email"
              name="email"
              placeholder="Email Id"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
        </div>
        <div className="input-title">
          <div>
            <h5>Date of Birth</h5>
          </div>
          <div className="date-input">
            <input
              type="text"
              placeholder="DD/MM/YYYY"
              onClick={handleDateInputClick}
              readOnly
            />

            {/* Hidden date input field */}
            <input
              type="date"
              ref={dateInputRef}
              onChange={(e) => {
                const selectedDate = e.target.value;
                console.log("Selected Date:", selectedDate);
                // Update your state or perform other actions with the selected date
              }}
            />
          </div>
        </div>
      </div>
    </>
  );
}

export default SignUpDetails;
