import React from "react";
import { useEffect } from "react";

import img1 from "../../assets/img/sliderImg/img1.png";
import img2 from "../../assets/img/sliderImg/img2.png";
import img3 from "../../assets/img/sliderImg/img3.png";
import img4 from "../../assets/img/sliderImg/img4.png";
import img5 from "../../assets/img/sliderImg/img5.png";
import arrow from "../../assets/img/sliderImg/arrow.png";

function Slider() {
  useEffect(() => {
    var swiper = new window.Swiper(".mySwiper", {
      effect: "coverflow",
      grabCursor: true,
      centeredSlides: true,
      slidesPerView: 3,
      coverflowEffect: {
        rotate: 15,
        stretch: 75,
        depth: 300,
        modifier: 1,
        slideShadows: true,
      },
      loop: true,
      navigation: {
        nextEl: ".swiper-button-next",
        prevEl: ".swiper-button-prev",
      },
    });

    // Cleanup Swiper instance on component unmount
    return () => {
      swiper.destroy();
    };
  }, []);
  return (
    <>
      <div class="swiper mySwiper">
        <div class="swiper-wrapper">
          <div class="swiper-slide">
            <h3>Tv Serial</h3>
            <img src={img1} alt="" />
          </div>
          <div class="swiper-slide">
            <img src={img2} alt="" />
            <h3>Bollywood</h3>
          </div>
          <div class="swiper-slide">
            <img src={img3} alt="" />
            <h3>Music Company</h3>
          </div>
          <div class="swiper-slide">
            <img src={img4} alt="" />
            <h3>Soloist</h3>
          </div>
          <div class="swiper-slide">
            <img src={img5} alt="" />
            <h3>Short Flim</h3>
          </div>
          <div class="swiper-slide">
            <img src={img2} alt="" />
            <h3>Bollywood</h3>
          </div>
          <div class="swiper-slide">
            <img src={img3} alt="" />
            <h3>Music Company</h3>
          </div>
          <div class="swiper-slide">
            <img src={img4} alt="" />
            <h3>Soloist</h3>
          </div>
          <div class="swiper-slide">
            <img src={img5} alt="" />
            <h3>Short Flim</h3>
          </div>
        </div>
      </div>
      <div class="swiper-button-next">
        <img src={arrow} alt="" />
      </div>
      <div class="swiper-button-prev">
        <img src={arrow} alt="" />
      </div>
    </>
  );
}

export default Slider;
