import React, { useEffect, useState } from "react";

function SignUpDetails(props) {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [date, setDate] = useState("");
  useEffect(() => {
    // props.sendData is a dependency for this useEffect
    // so this code will run whenever props.sendData changes

    if (props.sendData) {
      if (email === "sonu@gmail.com") {
        alert("This email is already present");
      } else {
        alert("Form submitted");
      }
    }
  }, [props.sendData, email]);

  return (
    <>
      <div className="enter-mob-inner-div3">
        <div className="input-title">
          <div>
            <h5>Enter your Full Name</h5>
          </div>
          <div>
            <input
              type="text"
              name="name"
              placeholder="Full Name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
            />
          </div>
        </div>
        <div className="input-title">
          <div>
            <h5>Enter your Email Id</h5>
          </div>
          <div>
            <input
              type="email"
              name="email"
              placeholder="Email Id"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
        </div>
        <div className="input-title">
          <div>
            <h5>Date of Birth</h5>
          </div>
          <div>
            <input
              type="date"
              name="date"
              value={date}
              onChange={(e) => setDate(e.target.value)}
              required
            />
          </div>
        </div>
      </div>
    </>
  );
}

export default SignUpDetails;
