import React, { useState, useEffect } from "react";
import { FaAngleRight } from "react-icons/fa6";

export default function ChooseBudget() {
  const [minRange, setMinRange] = useState(0);
  const [maxRange, setMaxRange] = useState(20);

  const minRangeValueGap = 20;

  const handleInputChange = (e) => {
    const { name, value } = e.target;

    let newMinRange = parseInt(value);
    let newMaxRange = maxRange;

    // Ensure that newMinRange does not go below 0
    if (newMinRange < 0) {
      newMinRange = 0;
    }

    // Ensure that newMinRange does not go beyond newMaxRange - minRangeValueGap
    if (newMinRange > newMaxRange - minRangeValueGap) {
      newMinRange = newMaxRange - minRangeValueGap;
    }

    setMinRange(newMinRange);
  };

  useEffect(() => {
    const track = document.getElementById("range_track");
    const minInput = document.querySelector("input[name='min']");
    const maxInput = document.querySelector("input[name='max']");

    const trackWidth = track.offsetWidth;
    const minPercentage = (minRange / 100) * 100;
    const maxPercentage = 100 - (maxRange / 100) * 100;

    track.style.left = `${minPercentage}%`;
    track.style.right = `${maxPercentage}%`;

    minInput.style.zIndex = minRange > maxRange - minRangeValueGap ? "2" : "1";
    maxInput.style.zIndex = minRange < maxRange - minRangeValueGap ? "2" : "1";
  }, [minRange, maxRange]);

  return (
    <>
      <div className="project-div2">
        <div>
          <h2>Choose Type</h2>
        </div>
        <div className="choose-type-div">
          <div className="double_range_slider_box">
            <div className="double_range_slider">
              <span className="range_track" id="range_track"></span>

              <input
                type="range"
                name="min"
                min="0"
                max="100"
                value={minRange}
                step="1"
                onChange={handleInputChange}
              />
              <input
                type="range"
                name="max"
                min="0"
                max="100"
                value={maxRange}
                step="1"
                onChange={(e) => setMaxRange(parseInt(e.target.value))}
              />

              <div
                className="minvalue"
                style={{ left: `${(minRange / 100) * 100}%` }}
              >
                {minRange}
              </div>
              <div
                className="maxvalue"
                style={{ right: `${(1 - maxRange / 100) * 100}%` }}
              >
                {maxRange}
              </div>
            </div>
          </div>
        </div>

        <div className="project-div2-btn">
          <button>
            Continue <FaAngleRight />
          </button>
        </div>
      </div>
    </>
  );
}
