import React from "react";

function Footer() {
  return (
    <>
      <div
        className={`${
          sign == 1 ? "hr-line" : sign == 2 ? "visiblity" : "visiblity"
        }`}
      >
        <div></div>
        <small>OR</small>
        <div></div>
      </div>

      <div
        className={`${
          sign == 1 ? "signin-option" : sign == 2 ? "visiblity" : "visiblity"
        }`}
      >
        <div onClick={() => handleFirebaseClick(googleProvider, "GOOGLE")}>
          <img src={google} alt="Google" />
          <small>Sign in with Google </small>
        </div>
        <div onClick={() => handleFirebaseClick(facebookProvider, "FACEBOOK")}>
          <img src={facebook} alt="Facebook" />
        </div>
        <div>
          <img src={apple} alt="Apple" />
        </div>
      </div>
      <div
        className={`${
          sign == 1
            ? "continue"
            : sign == 2
            ? "verify-continue2 continue "
            : " continue "
        }`}
      >
        <div>
          <button type="sumbit" onClick={handleContinueButtonClick}>
            Continue
          </button>
        </div>
        <div>
          <h6>
            By creating an account or logging in, you agree to Choira's <br />{" "}
            <span>Conditions of Use</span> and
            <span>Privacy Policy.</span>
          </h6>
        </div>
      </div>
    </>
  );
}

export default Footer;
