import React from "react";
import "swiper/swiper.scss";
import "swiper/components/effect-coverflow/effect-coverflow.scss";
import "swiper/components/navigation/navigation.scss";
import "swiper/components/pagination/pagination.scss";
import SwiperCore, { EffectCoverflow, Navigation, Pagination } from "swiper";
import "swiper/swiper.min.css";

import logo from "../../assets/img/logo-choira.svg";
import o4 from "../../assets/img/o4.png";
import img1 from "../../assets/img/sliderImg/img1.png";
import img2 from "../../assets/img/sliderImg/img2.png";
import img3 from "../../assets/img/sliderImg/img3.png";
import img4 from "../../assets/img/sliderImg/img4.png";
import img5 from "../../assets/img/sliderImg/img5.png";

// Import Swiper React components
import { Swiper, SwiperSlide } from "swiper/react";

// Import Swiper styles
import "swiper/swiper.min.css";

// Import additional styles
import "swiper/components/effect-coverflow/effect-coverflow.min.css";
import "swiper/components/navigation/navigation.min.css";
import "swiper/components/pagination/pagination.min.css";

// Install Swiper modules
SwiperCore.use([EffectCoverflow, Navigation, Pagination]);

function Project() {
  function nextSlide() {
    const currentSlide = document.querySelector('input[name="slider"]:checked');
    const nextSlide =
      currentSlide.nextElementSibling ||
      document.querySelector('input[name="slider"]:first-of-type');

    nextSlide.checked = true;
  }

  function prevSlide() {
    const currentSlide = document.querySelector('input[name="slider"]:checked');
    const prevSlide =
      currentSlide.previousElementSibling ||
      document.querySelector('input[name="slider"]:last-of-type');

    prevSlide.checked = true;
  }

  return (
    <>
      <div
        className="wrapper"
        style={{ backgroundImage: "url(images/image-17.png)" }}
      >
        <div className="navbar">
          <div>
            <img src={logo} alt="Choira Logo" style={{ cursor: "pointer" }} />
          </div>
          <div className="list">
            <h3>Home</h3>
            <h3>Soundtracks</h3>
            <h3>Jam</h3>
            <h3>Projects</h3>
            <h3>Signin</h3>
            <img className="o4" src={o4} alt="" />
          </div>
        </div>
        <div className="main">
          <Swiper
            effect="coverflow"
            grabCursor={true}
            centeredSlides={true}
            loop={true}
            slidesPerView={"auto"}
            coverflowEffect={{
              rotate: 0,
              stretch: 0,
              depth: 100,
              modifier: 2.5,
            }}
            pagination={{ el: ".swiper-pagination", clickable: true }}
            navigation={{
              nextEl: ".swiper-button-next",
              prevEl: ".swiper-button-prev",
              clickable: true,
            }}
            className="swiper_container"
          >
            <SwiperSlide>
              <img src={img1} alt="img1" />
            </SwiperSlide>
            <SwiperSlide>
              <img src={img2} alt="img2" />
            </SwiperSlide>
            <SwiperSlide>
              <img src={img3} alt="img3" />
            </SwiperSlide>
            <SwiperSlide>
              <img src={img4} alt="img4" />
            </SwiperSlide>
            <SwiperSlide>
              <img src={img5} alt="img5" />
            </SwiperSlide>
            <div className="slider-controler">
              <div className="swiper-button-prev slider-arrow"></div>
            </div>
          </Swiper>
        </div>
      </div>
    </>
  );
}

export default Project;
