import React, { useState, useEffect } from "react";
import singer from "../../assets/img/singer2.png";
import "../home/signin.css";
import logo from "../../assets/img/logo-choira.svg";
import google from "../../assets/img/google.png";
import facebook from "../../assets/img/facebook.png";
import apple from "../../assets/img/apple.png";
import SigninNum from "../../components/signin/SigninNum";

function Signin() {
  const sign = true;

  const TimerComponent = () => {
    const [seconds, setSeconds] = useState(30);

    useEffect(() => {
      const timer = setTimeout(() => {
        setSeconds(0);
      }, 30000); // 30 seconds in milliseconds

      // Clear the timeout when the component is unmounted
      return () => clearTimeout(timer);
    }, []);

    return (
      <div>
        {seconds > 0 ? (
          <h6>Expect code in {seconds} seconds</h6>
        ) : (
          <h6>Code expected! Time is up!</h6>
        )}
      </div>
    );
  };

  return (
    <>
      <div className="navbar">
        <img src={logo} alt="" />
      </div>

      <div className="wrapper">
        <div className="main">
          <div className="singer">
            <img src={singer} />
          </div>

          <div className="signup">
            <div className="signup-main">
              <div className="signup-main-2">
                {/* ... (rest of your component) */}
                <div className="verify2-input">
                  <input type="text" />
                  <input type="text" />
                  <input type="text" />
                  <input type="text" />
                </div>
                {/* Include TimerComponent here */}
                <TimerComponent />
                {/* ... (rest of your component) */}
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default Signin;
