import React, { useState, useEffect } from "react";
import image1 from "./image1.jpg"; // Import your image files
import image2 from "./image2.jpg";
import image3 from "./image3.jpg";
// import "./Slider.css"; // Import your CSS file

const Slider = () => {
  const images = [image1, image2, image3];
  const [currentImage, setCurrentImage] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      // Change the image every 3 seconds (adjust as needed)
      setCurrentImage((prevImage) => (prevImage + 1) % images.length);
    }, 3000);

    return () => clearInterval(interval); // Cleanup interval on component unmount
  }, [images.length]);

  return (
    <div className="slider-container">
      <div
        className="slider-image"
        style={{ backgroundImage: `url(${images[currentImage]})` }}
      ></div>
      <div
        className="slider-image"
        style={{
          backgroundImage: `url(${images[(currentImage + 1) % images.length]})`,
        }}
      ></div>
      <div
        className="slider-image"
        style={{
          backgroundImage: `url(${images[(currentImage + 2) % images.length]})`,
        }}
      ></div>
    </div>
  );
};

export default Slider;
