import React, { useState } from "react";
import { FaAngleRight } from "react-icons/fa6";

const ProjectDelivery = () => {
  const [selectedOption, setSelectedOption] = useState(null);

  const options = [
    { days: 7, cost: "+ ₹ 0" },
    { days: 6, cost: "+ ₹ 2,500" },
    { days: 5, cost: "+ ₹ 5,000" },
  ];

  const calculateDeliveryDate = (days) => {
    const currentDate = new Date();
    const deliveryDate = new Date(
      currentDate.setDate(currentDate.getDate() + days)
    );

    // Format the date as "Month day, year"
    const options = { month: "long", day: "numeric", year: "numeric" };
    const formattedDate = deliveryDate.toLocaleDateString("en-US", options);

    return formattedDate;
  };

  const handleOptionClick = (index) => {
    setSelectedOption(index);
  };

  return (
    <>
      <div className="project-div2">
        <div>
          <h2>When do you want your project delivered</h2>
        </div>
        <div className="project-delivery-main">
          {options.map((option, index) => (
            <div
              key={index}
              className={`project-option ${
                index === selectedOption ? "selected-option" : ""
              }`}
              onClick={() => handleOptionClick(index)}
            >
              <div>
                <h3>{`${option.days} day delivery (standard)`}</h3>
              </div>
              <div>
                <h4>{option.cost}</h4>
              </div>
              <div>
                <h3>
                  {option.days === 7
                    ? "March 29, 2023"
                    : calculateDeliveryDate(option.days)}
                </h3>
              </div>
            </div>
          ))}
        </div>

        <div className="project-div2-btn">
          <button>
            Continue <FaAngleRight />
          </button>
        </div>
      </div>
    </>
  );
};

export default ProjectDelivery;
