import React, { useState } from "react";
import { FaAngleRight } from "react-icons/fa6";

export default function ChooseBudget() {
  const [minRange, setMinRange] = useState(0);
  const [maxRange, setMaxRange] = useState(20);

  const minRangeValueGap = 20;

  const handleInputChange = (e) => {
    const { name, value } = e.target;

    let newMinRange = minRange;
    let newMaxRange = maxRange;

    if (name === "min") {
      newMinRange = parseInt(value);
    } else if (name === "max") {
      newMaxRange = parseInt(value);
    }

    // Ensure that minRange does not go beyond maxRange
    if (newMinRange > newMaxRange - minRangeValueGap) {
      newMinRange = newMaxRange - minRangeValueGap;
    }

    setMinRange(newMinRange);
    setMaxRange(newMaxRange);
  };

  return (
    <>
      <div className="project-div2">
        <div>
          <h2>Choose Type</h2>
        </div>
        <div className="choose-type-div">
          <div className="double_range_slider_box">
            <div className="double_range_slider">
              <span
                className="range_track"
                id="range_track"
                style={{
                  left: `${(minRange / 100) * 100}%`,
                  right: `${(1 - maxRange / 100) * 100}%`,
                }}
              ></span>

              <input
                type="range"
                name="min"
                min="0"
                max="100"
                value={minRange}
                step="1"
                onChange={handleInputChange}
              />
              <input
                type="range"
                name="max"
                min="0"
                max="100"
                value={maxRange}
                step="1"
                onChange={handleInputChange}
              />

              <div
                className="minvalue"
                style={{ left: `${(minRange / 100) * 100}%` }}
              >
                {`₹${minRange}k`}
              </div>
              <div
                className="maxvalue"
                style={{ right: `${(1 - maxRange / 100) * 100}%` }}
              >
                {`${maxRange}k`}
              </div>
            </div>
          </div>
        </div>

        <div className="project-div2-btn">
          <button>
            Continue <FaAngleRight />
          </button>
        </div>
      </div>
    </>
  );
}
