// CustomCalendar.js
import React, { useState } from "react";
// import "./CustomCalendar.css"; // Create this file for styling if needed

const CustomCalendar = () => {
  // Use state to track the current date
  const [currentDate, setCurrentDate] = useState(new Date());

  // Function to handle navigation to the next day
  const goToNextDay = () => {
    const newDate = new Date(currentDate);
    newDate.setDate(newDate.getDate() + 1);

    // If the next day is in the next month, update the month as well
    if (newDate.getMonth() !== currentDate.getMonth()) {
      newDate.setMonth(newDate.getMonth() + 1);
    }

    setCurrentDate(newDate);
  };

  // Function to handle navigation to the next month
  const goToNextMonth = () => {
    const newDate = new Date(currentDate);
    newDate.setMonth(newDate.getMonth() + 1);
    setCurrentDate(newDate);
  };

  // Function to get the days of the month for the current date
  const getDaysInMonth = () => {
    const daysInMonth = [];
    const daysOfWeek = ["Sun", "Mon", "Tue", "Wed", "Thu", "Fri", "Sat"];

    const firstDay = new Date(
      currentDate.getFullYear(),
      currentDate.getMonth(),
      1
    );
    const startDayOfWeek = firstDay.getDay(); // 0 (Sunday) to 6 (Saturday)

    // Add empty placeholders for days before the first day of the month
    for (let i = 0; i < startDayOfWeek; i++) {
      daysInMonth.push("");
    }

    // Add the days of the month
    for (let i = 1; i <= 7; i++) {
      const day = new Date(
        currentDate.getFullYear(),
        currentDate.getMonth(),
        i
      );
      daysInMonth.push({
        date: day.getDate(),
        dayOfWeek: daysOfWeek[day.getDay()],
      });
    }

    return daysInMonth;
  };

  return (
    <div className="custom-calendar">
      <div className="calendar-header">
        <button onClick={goToNextDay}>{">"}</button>
        <button onClick={goToNextMonth}>{">>"}</button>
        <h2>
          {new Intl.DateTimeFormat("en-US", {
            month: "long",
            year: "numeric",
          }).format(currentDate)}
        </h2>
      </div>
      <div className="calendar-days">
        {getDaysInMonth().map((day, index) => (
          <div key={index} className="day">
            {day.dayOfWeek}
          </div>
        ))}
      </div>
      <div className="calendar-dates">
        {getDaysInMonth().map((day, index) => (
          <div key={index} className="date">
            {day.date}
          </div>
        ))}
      </div>
    </div>
  );
};

export default CustomCalendar;
