// CustomCalendar.js
import React, { useState } from "react";
// import "./CustomCalendar.css"; // Create this file for styling if needed

const CustomCalendar = () => {
  // Use state to track the current month and year
  const [currentDate, setCurrentDate] = useState(new Date());

  // Function to handle navigation to the previous month
  const goToPreviousMonth = () => {
    const newDate = new Date(currentDate);
    newDate.setMonth(newDate.getMonth() - 1);
    setCurrentDate(newDate);
  };

  // Function to handle navigation to the next month
  const goToNextMonth = () => {
    const newDate = new Date(currentDate);
    newDate.setMonth(newDate.getMonth() + 1);
    setCurrentDate(newDate);
  };

  // Function to get the days of the month for the current date
  const getDaysInMonth = () => {
    const firstDay = new Date(
      currentDate.getFullYear(),
      currentDate.getMonth(),
      1
    );
    const lastDay = new Date(
      currentDate.getFullYear(),
      currentDate.getMonth() + 1,
      0
    );
    const daysInMonth = [];

    for (let i = 0; i < 7; i++) {
      const day = new Date(firstDay);
      day.setDate(firstDay.getDate() + i);
      daysInMonth.push(day.getDate());
    }

    return daysInMonth;
  };

  return (
    <div className="custom-calendar">
      <div className="calendar-header">
        <button onClick={goToPreviousMonth}>{"<"}</button>
        <h2>
          {new Intl.DateTimeFormat("en-US", {
            month: "long",
            year: "numeric",
          }).format(currentDate)}
        </h2>
        <button onClick={goToNextMonth}>{">"}</button>
      </div>
      <div className="calendar-days">
        <div className="day">Sun</div>
        <div className="day">Mon</div>
        <div className="day">Tue</div>
      </div>
      <div className="calendar-dates">
        {getDaysInMonth().map((day, index) => (
          <div key={index} className="date">
            {day}
          </div>
        ))}
      </div>
    </div>
  );
};

export default CustomCalendar;
