import React from "react";
import { useEffect } from "react";

import img1 from "../../assets/img/sliderImg/img1.png";
import img2 from "../../assets/img/sliderImg/img2.png";
import img3 from "../../assets/img/sliderImg/img3.png";
import img4 from "../../assets/img/sliderImg/img4.png";
import img5 from "../../assets/img/sliderImg/img5.png";
import arrow from "../../assets/img/sliderImg/arrow.png";
const swiperData = [
  {
    title: "Tv Serial",
    imageSrc: img1,
  },
  {
    title: "Bollywood",
    imageSrc: img2,
  },
  {
    title: "Music Company",
    imageSrc: img3,
  },
  {
    title: "Soloist",
    imageSrc: img4,
  },
  {
    title: "Short Film",
    imageSrc: img5,
  },
  {
    title: "Bollywood",
    imageSrc: img2,
  },
  {
    title: "Music Company",
    imageSrc: img3,
  },
  {
    title: "Soloist",
    imageSrc: img4,
  },
  {
    title: "Short Film",
    imageSrc: img5,
  },
  {
    title: "Short Film",
    imageSrc: img5,
  },
  {
    title: "Short Film",
    imageSrc: img5,
  },
];

function Slider() {
  useEffect(() => {
    var swiper = new window.Swiper(".mySwiper", {
      effect: "coverflow",
      grabCursor: true,
      centeredSlides: true,
      slidesPerView: 3,
      coverflowEffect: {
        rotate: 15,
        stretch: 75,
        depth: 300,
        modifier: 1,
        slideShadows: true,
      },
      loop: true,
      navigation: {
        nextEl: ".swiper-button-next",
        prevEl: ".swiper-button-prev",
      },
    });

    // Cleanup Swiper instance on component unmount
    return () => {
      swiper.destroy();
    };
  }, []);
  return (
    <>
      <div className="swiper mySwiper">
        <div className="swiper-wrapper">
          {swiperData.map((item, index) => (
            <div className="swiper-slide" key={index}>
              <h3>{item.title}</h3>
              <img src={item.imageSrc} alt="" />
            </div>
          ))}
        </div>
      </div>
      <div class="swiper-button-next">
        <img src={arrow} alt="" />
      </div>
      <div class="swiper-button-prev">
        <img src={arrow} alt="" />
      </div>
    </>
  );
}

export default Slider;
