import React from "react";
import "../home/project.css";
import logo from "../../assets/img/logo-choira.svg";
import o4 from "../../assets/img/o4.png";

function Project() {
  function nextSlide() {
    const currentSlide = document.querySelector('input[name="slider"]:checked');
    const nextSlide =
      currentSlide.nextElementSibling ||
      document.querySelector('input[name="slider"]:first-of-type');

    nextSlide.checked = true;
  }

  function prevSlide() {
    const currentSlide = document.querySelector('input[name="slider"]:checked');
    const prevSlide =
      currentSlide.previousElementSibling ||
      document.querySelector('input[name="slider"]:last-of-type');

    prevSlide.checked = true;
  }
  return (
    <>
      <div
        className="wrapper"
        style={{ backgroundImage: "url(images/image-17.png)" }}
      >
        <div className="overlay"></div>
        <div className="navbar">
          <div>
            <img src={logo} alt="Choira Logo" style={{ cursor: "pointer" }} />
          </div>
          <div className="list">
            <h3>Home</h3>
            <h3>Soundtracks</h3>
            <h3>Jam</h3>
            <h3>Projects</h3>
            <h3>Signin</h3>

            <img className="o4" src={o4} alt="" />
          </div>
        </div>
        <div className="main">
          <section id="slider">
            {/* <!-- Radio buttons for navigation --> */}
            <input type="radio" name="slider" id="s1" checked />
            <input type="radio" name="slider" id="s3" checked />
            <input type="radio" name="slider" id="s4" checked />
            <input type="radio" name="slider" id="s5" checked />

            {/* <!-- Slide labels with images --> */}
            <label for="s1" id="slide1">
              <div>
                <img
                  src="https://i.pinimg.com/originals/2b/de/de/2bdede0647e3cdf75b44ea33723201d9.jpg"
                  alt=""
                />
              </div>
            </label>
            <label for="s2" id="slide2">
              <img
                src="https://images6.alphacoders.com/462/thumb-1920-462371.jpg"
                alt=""
              />
            </label>
            <label for="s3" id="slide3">
              <img src="https://wallpaperaccess.com/full/1154341.jpg" alt="" />
            </label>
            <label for="s4" id="slide4">
              <img src="https://wallpapercave.com/wp/wp2634222.jpg" alt="" />
            </label>
            <label for="s5" id="slide5">
              <img
                src="https://images5.alphacoders.com/343/thumb-1920-343645.jpg"
                alt=""
              />
            </label>

            {/* <!-- Previous and Next buttons --> */}
            <button onClick={prevSlide}>Previous</button>
            <button onclick={nextSlide}>Next</button>
          </section>
        </div>
      </div>
    </>
  );
}

export default Project;
